mapper.select:
select id, parent_id as parentId, product_id as productId, name, type, creator from sepp_case_tree where 1 = 1
and id = #{id}
and parent_id = #{parentId}
and product_id = #{productId}
order by id desc

select min(id)
from sepp_case_tree
where product_id = #{productId}
  and type = #{type}
  and name = #{folderName}
  and parent_id = #{parentId}

mapper.update:
update
    sepp_case_info
set
    status = #{status}
where
    case_id = #{caseId}

mapper.select:
select mind_text
  from sepp_case_mind
 where case_id = #{caseId}

select
    relate_id as relateId,
    id
from
    sepp_case_related
where 1 = 1


        and relate_id=#{relateId}


        and case_id = #{caseId}
        and relate_type = #{relateType}


        and 1 = 2

mapper.delete:
delete from
    sepp_case_related
where case_id = #{caseId}
  and relate_id = #{id}
  and relate_type = #{relateType}
